mapper.select:
select *
from menu 
where 1=1

    and menu_cd = #{menu_cd}


    and parent_menu_cd = #{parent_menu_cd}

order by ord_no

select
  a.seq_no,
  a.id,
  a.name,
  a.jikbun_cd,
  fun_cd_nm(a.jikbun_cd) as jikbun_cd_nm, 
  a.email,
  fun_fmt_date(a.reg_dt) as reg_dt,
  a.del_yn,
  a.mw_cd,
  fun_cd_nm(a.mw_cd) as mw_cd_nm
from member a
where 1=1

  and a.seq_no = #{seq_no}


  and a.id = #{id}


  and a.name = #{name}


  and a.jikbun_cd = #{jikbun_cd}


  and a.mw_cd = #{mw_cd}


  and a.reg_dt  >=  #{start_reg_dt}


  and a.reg_dt  <=  #{end_reg_dt}

mapper.update:
update code set
  cd_nm = #{cd_nm},
  ord_no = #{ord_no}
where cd = #{cd}

update address set
  postal_cd = #{postal_cd},
  addr = #{addr},
  addr_detail = #{addr_detail}
where seq_no = #{seq_no}

mapper.select:
select seq_no, src_tbl_nm, rf_key, file_kind_cd, file_real_path, file_path, file_nm, file_org_nm from file where 1=1
and seq_no = #{seq_no}
and src_tbl_nm = #{src_tbl_nm}
and rf_key = #{rf_key}
